import React from 'react';

const API_KEY = "c1d6ebdfab337a5f6d26dd1abbb4ad09";
const API_ID = "512e40ea";

class Recipe extends React.Component {
    state = {
        activeRecipe: []
    }
    componentDidMount = async () => {
        const title = this.props.location.state.recipe;
        const req = await fetch
        (`https://cors-anywhere.herokuapp.com/https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=${title}`);

        const res = await req.json();
        this.setState({ activeRecipe: res.hits[0]});
    }
    render() {
        const recipe = this.state.activeRecipe;
        console.log(this.state.activeRecipe.recipe);
        console.log(this.props);
        //console.log(recipe.recipe);
        return (
            <div className="container">
                <div className="active-recipe">
                    <img className="active-recipe_img" src={recipe.image} alt={recipe.label}/>
                    <h3 className="active-recipe_title">{recipe.calories}</h3>
                    <h4 className="active-recipe_publisher"> 
                        <span>
                           {recipe.calories} 
                        </span>
                    </h4>
                    <p className="active-recipe_website">
                        Website: <span><a href={recipe.url}>{recipe.url}</a></span>
                    </p>
                    <button className="active-recipe_button">Go Home</button>
                </div>
            </div>             
        );
    }
};


export default Recipe;
