import React from 'react';

const API_KEY = "c1d6ebdfab337a5f6d26dd1abbb4ad09";
const API_ID = "512e40ea";

class Recipe extends React.Component {
    state = {
        activeRecipe: []
    }
    componentDidMount = async () => {
        const title = this.props.location.state.recipe;
        const request = await fetch
        (`https://cors-anywhere.herokuapp.com/https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=${title}`);

        const result = await request.json();
        this.setState({ activeRecipe: result.hits[0]});
        console.log(this.state.activeRecipe);
    }
    render() {
        const recipe = this.state.activeRecipe;
        return (
            <div className="container">
                <div className="active-recipe">
                    <img className="active-recipe_img" src={recipe.recipe.image} alt={recipe.recipe.label}/>
                    <h3 className="active-recipe_title">{recipe.recipe.label}</h3>
                </div>
            </div>
        );
    }
}


export default Recipe;
